mapper.insert:
INSERT INTO parts_gpu
(
	id
	, parts_name
	, parts_price
	, gled_cd
	, gn
	, gmc_cd
	, gsc_cd
	, gsv
	, gpuas_cd
	, qc
	, tdp
	, bn
	, il
	, gpl
	, twelve_pin
	, parts_image
	, reg_dtm
	, updt_dtm
	, multi_bulk
)
VALUES
(
	#{id}
	, #{partsName}
	, #{partsPrice}
	, #{gledCd}
	, #{gn}
	, #{gmcCd}
	, #{gscCd}
	, #{gsv}
	, #{gpuasCd}
	, #{qc}
	, #{tdp}
	, #{bn}
	, #{il}
	, #{gpl}
	, #{twelvePin}
	, #{partsImage}
	, NOW()
	, #{updtDtm}
	, #{multiBulk}
)

mapper.update:
UPDATE
	parts_gpu
SET
	parts_name            = #{partsName} 
	, parts_price         = #{partsPrice}
	, gled_cd             = #{gledCd}    
	, gn                  = #{gn}        
	, gmc_cd              = #{gmcCd}     
	, gsc_cd              = #{gscCd}     
	, gsv                 = #{gsv}       
	, gpuas_cd            = #{gpuasCd}   
	, qc                  = #{qc}        
	, tdp                 = #{tdp}       
	, bn                  = #{bn}        
	, il                  = #{il}        
	, gpl                 = #{gpl}       
	, twelve_pin          = #{twelvePin} 
	, parts_image         = #{partsImage}
	, updt_dtm            = NOW()        
	, multi_bulk          = #{multiBulk} 
WHERE
	id = #{id}

