mapper.insert:
INSERT INTO order_master
(
	id
	, order_date
	, order_name
	, tot_order_price
	, order_state_cd
	, orderer_user_id
	, orderer_name
	, orderer_hp_number
	, orderer_mail
	, recipient_name
	, recipient_hp_number
	, recipient_hp_number2
	, recipient_jibun_addr
	, recipient_road_addr
	, recipient_detail_addr
	, recipient_zipcode
	, order_request
	, delivery_request
	, payment_method
	, waybill_number
	, video_request_cd
	, reg_dtm
)
VALUES
(
	#{id}
	, NOW()
	, #{orderName}
	, #{totOrderPrice}
	, #{orderStateCd}
	, #{ordererUserId}
	, #{ordererName}
	, #{ordererHpNumber}
	, #{ordererMail}
	, #{recipientName}
	, #{recipientHpNumber}
	, #{recipientHpNumber2}
	, #{recipientJibunAddr}
	, #{recipientRoadAddr}
	, #{recipientDetailAddr}
	, #{recipientZipcode}
	, #{orderRequest}
	, #{deliveryRequest}
	, #{paymentMethod}
	, #{waybillNumber}
	, #{videoRequestCd}
	, NOW()
)

mapper.update:
UPDATE
	order_master
SET
	order_date               = #{orderDate}          
	, order_name             = #{orderName}          
	, tot_order_price        = #{totOrderPrice}      
	, order_state_cd         = #{orderStateCd}       
	, orderer_user_id        = #{ordererUserId}      
	, orderer_name           = #{ordererName}        
	, orderer_hp_number      = #{ordererHpNumber}    
	, orderer_mail           = #{ordererMail}        
	, recipient_name         = #{recipientName}      
	, recipient_hp_number    = #{recipientHpNumber}  
	, recipient_hp_number2   = #{recipientHpNumber2} 
	, recipient_jibun_addr   = #{recipientJibunAddr} 
	, recipient_road_addr    = #{recipientRoadAddr}  
	, recipient_detail_addr  = #{recipientDetailAddr}
	, recipient_zipcode      = #{recipientZipcode}   
	, order_request          = #{orderRequest}       
	, delivery_request       = #{deliveryRequest}    
	, payment_method         = #{paymentMethod}      
	, waybill_number         = #{waybillNumber}
	, video_request_cd       = #{videoRequestCd}      
	, updt_dtm               = NOW()
WHERE
	id = #{id}

mapper.insert:
INSERT INTO order_master_history
