	, #{recipientZipcode}
	, #{orderRequest}
	, #{deliveryRequest}
	, #{paymentMethod}
	, #{waybillNumber}
	, #{videoRequestCd}
	, NOW()
	, #{historyContents}
)

mapper.select:
SELECT a.id AS id , a.order_date AS orderDate , DATE_FORMAT(a.order_date, '%Y-%m-%d') AS orderDateStr , a.order_name AS orderName , a.tot_order_price AS totOrderPrice , FORMAT(a.tot_order_price, 0) AS totOrderPriceStr , a.order_state_cd AS orderStateCd , (SELECT nm FROM comn_cd_detail WHERE comn_cd = 'ORD001' AND cd = a.order_state_cd) AS orderStateCdNm , a.orderer_user_id AS ordererUserId , a.orderer_name AS ordererName , a.orderer_hp_number AS ordererHpNumber , a.orderer_mail AS ordererMail , a.recipient_name AS recipientName , a.recipient_hp_number AS recipientHpNumber , a.recipient_hp_number2 AS recipientHpNumber2 , a.recipient_jibun_addr AS recipientJibunAddr , a.recipient_road_addr AS recipientRoadAddr , a.recipient_detail_addr AS recipientDetailAddr , a.recipient_zipcode AS recipientZipcode , a.order_request AS orderRequest , a.delivery_request AS deliveryRequest , a.payment_method AS paymentMethod , a.waybill_number AS waybillNumber , a.video_request_cd AS videoRequestCd , (SELECT nm FROM comn_cd_detail WHERE comn_cd = 'ORD002' AND cd = a.video_request_cd) AS videoRequestCdNm , a.reg_dtm AS regDtm , a.updt_dtm AS updtDtm FROM order_master a
AND a.id = #{id}
AND a.orderer_user_id = #{ordererUserId}

mapper.insert:
INSERT INTO order_detail
(
	id
	, seq
	, product_id
	, product_price
	, product_order_qty
	, reg_dtm
	, box_qty
	, box_tot_price
)
VALUES
(
	#{id}
	, #{seq}
	, #{productId}
	, #{productPrice}
	, #{productOrderQty}
	, NOW()
	, #{boxQty}
	, #{boxTotPrice}
)

mapper.select:
SELECT a.id AS id , a.seq AS seq , a.product_id AS productId , a.product_price AS productPrice , a.product_order_qty AS productOrderQty , a.box_qty AS boxQty , a.box_tot_price AS boxTotPrice , a.reg_dtm AS regDtm , b.product_name AS productName , b.product_description AS productDescription , b.product_image AS productImage FROM order_detail a LEFT OUTER JOIN
( SELECT z.id AS id , z.product_name AS product_name , z.product_description AS product_description , z.product_image AS product_image FROM product_master z UNION ALL SELECT x.id AS id , x.banpum_name AS product_name , x.banpum_description AS product_description , x.banpum_image1 AS product_image FROM banpum_master x ) b ON a.product_id = b.id
AND a.id = #{id}
AND a.product_id = #{productId}

mapper.insert:
INSERT INTO refund_info
(
	id
	, order_id
	, order_seq
	, product_id
	, product_price
	, refund_qty
	, request_refund_price
	, determin_refund_price
	, refund_state_cd
	, refund_reason_cd
	, refund_reason_user_write
	, refund_content
	, refund_remarks
	, refund_partial_agree_content
	, refund_partial_agree_cd
	, reg_dtm
)
VALUES
(
	#{id}
	, #{orderId}
	, #{orderSeq}
	, #{productId}
	, #{productPrice}
	, #{refundQty}
	, #{requestRefundPrice}
	, #{determinRefundPrice}
	, #{refundStateCd}
	, #{refundReasonCd}
	, #{refundReasonUserWrite}
	, #{refundContent}
	, #{refundRemarks}
	, #{refundPartialAgreeContent}
	, #{refundPartialAgreeCd}
	, NOW()
)

mapper.update:
UPDATE
	refund_info
SET
	order_id                             = #{orderId}              
	, order_seq                          = #{orderSeq}             
	, product_id                         = #{productId}            
	, product_price                      = #{productPrice}         
	, refund_qty                         = #{refundQty}            
	, request_refund_price                   = #{requestRefundPrice}
	, determin_refund_price                   = #{determinRefundPrice}       
	, refund_state_cd                    = #{refundStateCd}        
	, refund_reason_cd                   = #{refundReasonCd}       
	, refund_reason_user_write           = #{refundReasonUserWrite}
	, refund_content                     = #{refundContent}        
	, refund_remarks                     = #{refundRemarks}
	, refund_partial_agree_content                     = #{refundPartialAgreeContent}
	, refund_partial_agree_cd                     = #{refundPartialAgreeCd}        
	, updt_dtm                           = NOW()                   
WHERE
	id = #{id}

mapper.select:
SELECT a.order_state_cd AS orderStateCd FROM order_master_history a WHERE a.id = #{id} AND a.history_seq = ( SELECT MAX(z.history_seq) FROM order_master_history z WHERE z.id = #{id}
AND z.order_state_cd < 09
GROUP BY z.id )
