mapper.insert:
INSERT INTO parts_maker
(
	id
	, maker_name
	, as_score
	, reg_dtm
	, updt_dtm
)
VALUES
(
	#{id}
	, #{makerName}
	, #{asScore}
	, NOW()
	, #{updtDtm}
)

mapper.select:
SELECT a.id AS id , a.maker_name AS makerName , a.as_score AS asScore , a.reg_dtm AS regDtm , a.updt_dtm AS updtDtm FROM parts_maker a
AND a.id = #{id}
ORDER BY a.maker_name

mapper.update:
UPDATE
	parts_maker
SET
	maker_name            = #{makerName} 
	, as_score            = #{asScore}
	, updt_dtm            = NOW()        
WHERE
	id = #{id}

mapper.insert:
INSERT INTO parts_maker_history
