mapper.insert:
INSERT INTO process_resource_type_cd_info
(
	process_type_exclusive_cd
	, process_type_exclusive_cd_nm
	, process_lg_cd
	, use_yn
	, reg_dtm
	, updt_dtm
)
VALUES
(
	#{processTypeExclusiveCd}
	, #{processTypeExclusiveCdNm}
	, #{processLgCd}
	, #{useYn}
	, NOW()
	, #{updtDtm}
)

mapper.select:
SELECT a.process_type_exclusive_cd AS processTypeExclusiveCd , a.process_type_exclusive_cd_nm AS processTypeExclusiveCdNm , a.process_lg_cd AS processLgCd , (SELECT nm FROM comn_cd_detail WHERE comn_cd = 'COM004' AND cd = a.process_lg_cd) AS processLgCdNm , a.use_yn AS useYn , a.reg_dtm AS regDtm , a.updt_dtm AS updtDtm FROM process_resource_type_cd_info a
AND a.process_type_exclusive_cd = #{processTypeExclusiveCd}
AND a.use_yn = 'Y'

mapper.update:
UPDATE
	process_resource_type_cd_info
SET
	process_type_exclusive_cd_nm            = #{processTypeExclusiveCdNm} 
	, use_yn              = #{useYn}    
	, updt_dtm            = NOW()        
WHERE
	process_type_exclusive_cd = #{processTypeExclusiveCd}

mapper.insert:
INSERT INTO process_resource_master
(
	id
	, process_lg_cd
	, process_type_exclusive_cd
	, process_name
	, reg_dtm
	, updt_dtm
)
VALUES
(
	#{id}
	, #{processLgCd}
	, #{processTypeExclusiveCd}
	, #{processName}
	, NOW()
	, #{updtDtm}
)

INSERT INTO process_resource_detail
(
	id
	, seq
	, variable_chk
	, resource_name
	, resource_mapping_value
	, resource_score
	, reg_dtm
	, updt_dtm
)
VALUES
(
	#{id}
	, #{seq}
	, #{variableChk}
	, #{resourceName}
	, #{resourceMappingValue}
	, #{resourceScore}
	, NOW()
	, #{updtDtm}
)

mapper.select:
SELECT a.id AS id , a.seq AS seq , b.process_type_exclusive_cd AS processTypeExclusiveCd , c.process_type_exclusive_cd_nm AS processTypeExclusiveCdNm , b.process_name AS processName , a.variable_chk AS variableChk , CASE WHEN a.variable_chk = 'F' THEN '고정' WHEN a.variable_chk = 'C' THEN 'CPU' WHEN a.variable_chk = 'G' THEN 'GVA' WHEN a.variable_chk = 'RM' THEN 'RAM(MaxRange)' WHEN a.variable_chk = 'RV' THEN 'RAM(Volume)' ELSE '알수없음' END variableChkNm , a.resource_name AS resourceName , a.resource_mapping_value AS resourceMappingValue , a.resource_score AS resourceScore , a.reg_dtm AS regDtm , a.updt_dtm AS updtDtm FROM process_resource_detail a LEFT OUTER JOIN process_resource_master b ON a.id = b.id LEFT OUTER JOIN process_resource_type_cd_info c ON b.process_type_exclusive_cd = c.process_type_exclusive_cd
AND a.id = #{id}
AND a.seq = #{seq}

mapper.update:
UPDATE
	process_resource_detail
SET
	resource_name        = #{resourceName}
	, resource_score        = #{resourceScore}    
	, updt_dtm            = NOW()        
WHERE
	id = #{id}
	AND seq = #{seq}

mapper.insert:
INSERT INTO process_resource_detail_history
(
	id
	, seq
	, history_seq
	, variable_chk
	, resource_name
	, resource_mapping_value
	, resource_score
	, reg_dtm
)
VALUES
(
	#{id}
	, #{seq}
	, #{historySeq}
	, #{variableChk}
	, #{resourceName}
	, #{resourceMappingValue}
	, #{resourceScore}
	, NOW()
)
